CREATE TABLE email (
    id integer GENERATED BY DEFAULT AS IDENTITY,
    subject text NULL,
    body text NULL,
    "to" text NULL,
    status integer NOT NULL,
    startsendingdate timestamp without time zone NULL,
    sentdate timestamp without time zone NULL,
    version integer NOT NULL,
    vieworder integer NOT NULL,
    CONSTRAINT "PK_email" PRIMARY KEY (id)
);

CREATE TABLE emailtemplate (
    id integer GENERATED BY DEFAULT AS IDENTITY,
    name text NULL,
    alias text NULL,
    subjecttemplate text NULL,
    bodytemplate text NULL,
    version integer NOT NULL,
    vieworder integer NOT NULL,
    CONSTRAINT "PK_emailtemplate" PRIMARY KEY (id)
);

CREATE UNIQUE INDEX "IX_emailtemplate_alias" ON emailtemplate (alias);

